import { Fragment } from "react";
import { useState } from "react";

function Counter() {
  const [counter] = useState(0);
  const [tags] = useState(["tag1", "tag2", "tag3"]);

  function formatCount() {
    return counter === 0 ? "Zero" : counter;
  }

  function getBadgeClasses() {
    let classes = "badge m-2 ";
    classes += counter === 0 ? "bg-warning" : "bg-primary";
    return classes;
  }

  function renderTags() {
    if (tags.length === 0) return <p>"There are No Tags!"</p>;
    return (
      <ul>
        {tags.map((tag) => (
          <li key={tag}> {tag} </li>
        ))}
      </ul>
    );
  }

  function handleIncrement() {
    console.log("Button Clicked!");
  }

  return (
    <Fragment>
      <h1 className={getBadgeClasses()}>{formatCount()}</h1>
      <button onClick={handleIncrement} className="btn btn-secondary btn-sm">
        Increment
      </button>
      <div>
        {tags.length === 0 && "Please create a new Tag!"}
        {renderTags()}
      </div>
    </Fragment>
  );
}

export default Counter;
