import { Fragment } from "react";
import { useState } from "react";

function Counter(props) {
  const [counter, setCounter] = useState(props.value);
  const [tags] = useState(["tag1", "tag2", "tag3"]);

  function formatCount() {
    return counter === 0 ? "Zero" : counter;
  }

  function getBadgeClasses() {
    let classes = "badge m-2 ";
    classes += counter === 0 ? "bg-warning" : "bg-primary";
    return classes;
  }

  function renderTags() {
    if (tags.length === 0) return <p>"There are No Tags!"</p>;
    return (
      <ul>
        {tags.map((tag) => (
          <li key={tag}> {tag} </li>
        ))}
      </ul>
    );
  }

  return (
    <Fragment>
      {props.children}
      <h4>Counter #{props.id}</h4>
      <h1 className={getBadgeClasses()}>{formatCount()}</h1>
      <button
        onClick={() => setCounter(counter + 1)}
        className="btn btn-secondary btn-sm"
      >
        Increment
      </button>
      <button
        onClick={() => handleDelete(props.id)}
        className="btn btn-danger btn-sm m-2"
      >
        Delete
      </button>
    </Fragment>
  );
}
// {/* <div>
//   {tags.length === 0 && "Please create a new Tag!"}
//   {renderTags()}
// </div> */}

export default Counter;
