import { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [posts, setPosts] = useState([])
  const apiEndoint = "https://jsonplaceholder.typicode.com/posts"

  async function getResults() {
    const results = await axios(apiEndoint);
    setGenres(results.data)
  }

  useEffect(() => {
    getResults()
  }, [])


  const handleAdd = async () => {
    const obj = { name: "Musical" }
    const results = await axios.post(apiEndoint, obj);
    setGenres(results, ...genres)
  };

  const handleUpdate = (genre) => {
    console.log("Update", genre);
  };

  const handleDelete = (genre) => {
    console.log("Delete", genre);
  };
  return (
    <div className="App">
      <button className="btn btn-primary mt-2" onClick={handleAdd}>
        Add
      </button>
      <table className="table">
        <thead>
          <tr>
            <th>Title</th>
            <th>Update</th>
            <th>Delete</th>
          </tr>
        </thead>
        <tbody>
          {genres.map(genre => (
            <tr key={genre._id}>
              <td>{genre.name}</td>
              <td>
                <button
                  className="btn btn-info btn-sm"
                  onClick={() => handleUpdate(genre)}
                >
                  Update
                </button>
              </td>
              <td>
                <button
                  className="btn btn-danger btn-sm"
                  onClick={() => handleDelete(genre)}
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default App;
