import { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [genres, setGenres] = useState([])

  async function getResults() {
    const results = await axios('http://codewithz.centralindia.cloudapp.azure.com:3900/api/genres');
    setGenres(results.data)
  }

  useEffect(() => {
    getResults()
  }, [])


  const handleAdd = () => {
    console.log("Add");
  };

  const handleUpdate = (genre) => {
    console.log("Update", genre);
  };

  const handleDelete = (genre) => {
    console.log("Delete", genre);
  };
  return (
    <div className="App">
      <button className="btn btn-primary" onClick={handleAdd}>
        Add
      </button>
      <table className="table">
        <thead>
          <tr>
            <th>Title</th>
            <th>Update</th>
            <th>Delete</th>
          </tr>
        </thead>
        <tbody>
          {genres.map(genre => (
            <tr key={genre.id}>
              <td>{genre.title}</td>
              <td>
                <button
                  className="btn btn-info btn-sm"
                  onClick={() => handleUpdate(genre)}
                >
                  Update
                </button>
              </td>
              <td>
                <button
                  className="btn btn-danger btn-sm"
                  onClick={() => handleDelete(genre)}
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default App;
