import { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

axios.interceptors.response.use(null, error => {
  const expectedError =
  error.response &
  error.response.status >= 400 &
  error.response.status < 500;

  if (expectedError) {
  console. log("Logging the error", error);
  alert("An unexpected error occurrred.");
  }})

function App() {
  const [posts, setPosts] = useState([])
  const apiEndoint = "https://jsonplaceholder.typicode.com/posts"

  async function getResults() {
    const results = await axios(apiEndoint);
    setPosts(results.data)
  }

  useEffect(() => {
    getResults()
  }, [])


  const handleAdd = async () => {
    const obj = { title: "a title", body: "a body" }
    const results = await axios.post(apiEndoint, obj);
    setPosts([results.data, ...posts])
  };

  const handleUpdate = async (post) => {
    post.title = "UPDATED A Title"
    await axios.put(apiEndoint + "/" + post.id, post);

    const index = posts.indexOf(post)
    posts[index].title = post.title

    setPosts(posts.map(p => {
      return p
    }))
  };

  const handleDelete = async (post) => {
    const originalPosts = posts

    setPosts(posts.filter((p) => p.id !== post.id));

    try {
      await axios.put(apiEndoint + "/" + post.id);
    } catch (error) {
      if (error.response && error.response.status === 404)
        alert("This post has already been deleted.");
      else {
        console.log("Logging the error", error);
        alert("An unexpected error occurrred.");
      }
      setPosts(originalPosts)
    };
  }

  return (
    <div className="App">
      <button className="btn btn-primary mt-2" onClick={handleAdd}>
        Add
      </button>
      <table className="table">
        <thead>
          <tr>
            <th>Title</th>
            <th>Update</th>
            <th>Delete</th>
          </tr>
        </thead>
        <tbody>
          {posts.map(post => (
            <tr key={post.id}>
              <td>{post.title}</td>
              <td>
                <button
                  className="btn btn-info btn-sm"
                  onClick={() => handleUpdate(post)}
                >
                  Update
                </button>
              </td>
              <td>
                <button
                  className="btn btn-danger btn-sm"
                  onClick={() => handleDelete(post)}
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default App;
