import { useState, useEffect, useMemo } from "react";
import { getMovies } from "./../services/fakeMovieService";
import { getGenres } from "../services/fakeGenreService";
import Like from "./common/like";
import Pagination from "./common/pagination";
import { Paginate } from "./utils/paginate";
import ListGroup from "./common/listGroup";

function Movies() {
  const [movies, setMovies] = useState([]);
  const [genres, setGenres] = useState(getGenres());
  const allGeneres = { _id: 0, name: "All Genres" };
  const [selectedGenre, setSelectedGenre] = useState(allGeneres);
  const [pageSize] = useState(4);
  const [currentPage, setCurrentPage] = useState(1);

  useMem
  
  useEffect(() => {
    setMovies(getMovies);
    setGenres([allGeneres, ...getGenres()]);
  }, [allGeneres]);
  console.log(genres)

  const filtered =
    selectedGenre && selectedGenre._id
      ? movies.filter((m) => m.genre._id === selectedGenre._id)
      : movies;

  const allMovies = Paginate(filtered, currentPage, pageSize);

  const handleDelete = (movie) => {
    setMovies(movies.filter((m) => m._id !== movie._id));
  };

  const handleLike = (movie) => {
    const index = movies.indexOf(movie);
    movies[index].liked = !movies[index].liked;
    setMovies(
      movies.map((m) => {
        return m;
      })
    );
  };

  const handleGenreSelect = (genre) => {
    setSelectedGenre(genre);
    setCurrentPage(1);
  };

  const handlePageChange = (page) => {
    setCurrentPage(page);
  };

  const { length: count } = movies;

  if (count === 0) return <p>There are No Movies in the Database</p>;

  return (
    <div className="row">
      <div className="col-3">
        <ListGroup
          items={genres}
          selectedItem={selectedGenre}
          onItemSelect={handleGenreSelect}
        />
      </div>
      <div className="col">
        <p>Showing {filtered.length} Movies in the Database</p>
        <table className="table">
          <thead>
            <tr>
              <th>Title</th>
              <th>Gnere</th>
              <th>Stock</th>
              <th>Rate</th>
              <th></th>
              <th></th>
            </tr>
          </thead>
          <tbody>
            {allMovies.map((movie) => (
              <tr key={movie._id}>
                <td>{movie.title}</td>
                <td>{movie.genre.name}</td>
                <td>{movie.numberInStock}</td>
                <td>{movie.dailyRentalRate}</td>
                <td>
                  <Like liked={movie.liked} onClick={() => handleLike(movie)} />
                </td>
                <td>
                  <button
                    onClick={() => handleDelete(movie)}
                    className="btn btn-danger btn-sm"
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
        <Pagination
          itemsCounts={filtered.length}
          pageSize={pageSize}
          currentPage={currentPage}
          onPageChange={handlePageChange}
        />
      </div>
    </div>
  );
}

export default Movies;
