import { useState } from "react";
import Joi from "joi-browser";

import { validate } from "./helpers/form";
import { handleChange } from "./helpers/form";

function LoginForm() {
  const [data, setData] = useState({ username: "", password: "" });
  const [errors, setErrors] = useState({});

  const schema = {
    username: Joi.string().required().label("Username"),
    password: Joi.string().required().label("Password"),
  };

  // const handleChange = (e) => {
  //   const { id, value } = e.target;
  //   setAccount((prevState) => ({
  //     ...prevState,
  //     [id]: value,
  //   }));

  //   const errorMessage = validateProperty({ id, value }, schema);
  //   if (errorMessage) {
  //     setErrors((prevState) => ({
  //       ...prevState,
  //       [id]: errorMessage,
  //     }));
  //   } else delete errors[id];
  // };

  // const validate = () => {
  //   const result = Joi.validate(account, schema, {
  //     abortEarly: false,
  //   });
  //   if (!result.error) return null;

  //   const formErrors = {};
  //   for (let item of result.error.details) {
  //     formErrors[item.path[0]] = item.message;
  //   }
  //   return formErrors;
  // };

  // const validateProperty = ({ id, value }) => {
  //   const obj = { [id]: value };
  //   const currentSchema = { [id]: schema[id] };
  //   const { error } = Joi.validate(obj, currentSchema);
  //   return error ? error.details[0].message : null;
  // };

  // const handleSubmit = (e) => {
  //   e.preventDefault();

  //   setErrors(validate(data, schema));
  //   if (errors) return;

  //   doSubmit();
  // };

  const doSubmit = () => {
    console.log("Submitted");
  };

  return (
    <div className="container">
      <h1>Login</h1>
      <form onSubmit={(e) => handleSubmit((e, data, schema, errors, setErrors, doSubmit))} className="container w-50 m-auto">
        <div className="form-group mt-3">
          <label htmlFor="username">Username</label>
          <input
            autoFocus
            onChange={(e) =>
              handleChange(e, schema, data, setData, errors, setErrors)
            }
            id="username"
            type="text"
            className="form-control mt-2"
          />
          {errors.username && (
            <div className="alert alert-danger">{errors.username}</div>
          )}
        </div>
        <div className="form-group mt-3">
          <label htmlFor="password">Password</label>
          <input
            onChange={(e) =>
              handleChange(e, schema, data, setData, errors, setErrors)
            }
            id="password"
            type="text"
            className="form-control mt-2"
          />
          {errors.password && (
            <div className="alert alert-danger">{errors.password}</div>
          )}
        </div>
        <div className="mt-3">
          <button disabled={validate(data, schema)} className="btn btn-primary">
            Login
          </button>
        </div>
      </form>
    </div>
  );
}

export default LoginForm;
