import { useEffect } from "react";
import { useNavigate } from "react-router-dom";

export const ProtectedRoute = (props) => {
  const navigate = useNavigate();
  useEffect(() => {
      if (!props.user) {
        navigate("/login");
      }
    
  }, [])
  
  return props.children;
};
