import { useState } from "react";
import { useNavigate } from "react-router-dom";
import Joi from "joi-browser";

import { handleSubmit, renderInput, renderButton } from "./helpers/form";
import { login } from "../services/authService";

function LoginForm() {
  const [data, setData] = useState({ username: "", password: "" });
  const [errors, setErrors] = useState({});
  const navigate = useNavigate();

  const schema = {
    username: Joi.string().required().label("Username"),
    password: Joi.string().required().label("Password"),
  };

  const doSubmit = async () => {
    try {
      const response = await login(data.username, data.password);
      
      window.location = ("/");
    } catch (error) {
      if (error.response && error.response.status === 400) {
        errors.username = error.response.data;
        setErrors({ username: error.response.data });
      }
    }
  };

  return (
    <div className="container">
      <h1>Login</h1>
      <form
        onSubmit={(e) =>
          handleSubmit(e, data, schema, errors, setErrors, doSubmit)
        }
        className="container w-50 m-auto"
      >
        {renderInput(
          "Username",
          "username",
          "text",
          schema,
          data,
          setData,
          errors,
          setErrors
        )}
        {renderInput(
          "Password",
          "password",
          "password",
          schema,
          data,
          setData,
          errors,
          setErrors
        )}
        {renderButton("Login", data, schema)}
      </form>
    </div>
  );
}

export default LoginForm;
